package Assignment1;
import java.util.*;
public class ABCPattern {

	public static void main(java.lang.String[] args) {
		// TODO Auto-generated method stub
		char i,j;
      for(i='A';i<='C';i++)
      {
    	  for(j='A';j<='C';j++)
    	  {
    		  System.out.print(i);
    	  }
    	  System.out.println();
      }
	}

}
