public class ReverseOrder {

	public static void main(java.lang.String[] args) {
		// TODO Auto-generated method stub
		 for(int row=1;row<=5;row++)
	       {
	    	   for(int col=row;col<=5;col++)
	    	   {
	    	   System.out.println("*");
	       }
	    	   System.out.println();
	    	   
	    	 
		}
	}

}
